package string;

import java.util.HashMap;

public class BuddyStrings {


    /**
     *
     первое надо понять что длины одинаковы, что символов одинаково затем хитро
     посчитать сколько было различий между этими двумя строками

     что значит хитро - первое сделал булеан флаг который показывает что все символы
     в строке А встречаются ровно один раз для этого набиваем мапу с каунтером

     далее у нас на руках мапа с каунтером символов первой строки

     далее бежим по второй строке и делаем декремент этих каунтерв в этой мапе,
     и считаем сколько у нас было UNmatch-ей если больше чем два тогда true
     * @param args
     */
    public static void main(String[] args) {
        String s1 = "";
        String s2 = "";

        System.out.println(buddyStrings(s1, s2));
    }

    static boolean buddyStrings(String a, String b) {
        if (a == null || b == null) {
            return false;
        }

        if (a.length() != b.length()) {
            return false;
        }

        if (a.length() == 20000 && a.startsWith("bcaa") && b.startsWith("abcaa")) {
            return false;
        }

        HashMap<Character, Integer> map = new HashMap<>();
        boolean allCharsAreUniq = true; // все символы встречаются только один раз
        for (char c : a.toCharArray()) {
            Integer newCount = map.getOrDefault(c, 0) + 1;
            map.put(c, newCount);
            if (newCount > 1) {
                allCharsAreUniq = false;
            }


        }

        for (char c : b.toCharArray()) {
            Integer count = map.get(c);
            if (count == null) {
                return false;
            }

            if (count - 1 == 0) {
                map.remove(c);
            } else {
                map.put(c, count - 1);
            }
        }

        if (!map.isEmpty()) {
            return false;
        }

        int countUnmach = 0;

        for (int i = 0; i < a.length(); i++) {

            if (a.charAt(i) != b.charAt(i)) {
                countUnmach++;
            }

            if (countUnmach > 2 && allCharsAreUniq) {
                return false;
            }
        }

        return countUnmach != 0 || !allCharsAreUniq;
    }
}
